package replit_forWhileDonguleri;


public class C08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * 'a' ile 'z' arasındaki karakterlerin ascii değerlerini yazdıran bir Java
		 * programı yazın.
		 * 
		 * Ascii value of a = 97 aradakilerin hepsi tek tek yazdirilecek Ascii value of
		 * z = 122
		 */

		for (char i = 97; i <= 122; i++) {
			
			int j=i;

			System.out.println("Ascii value of " + i + "="+ j);

		}
		
		}

	}

